import React, { useState,useEffect,useCallback } from "react";
import { sendMessageNotification,getUsers } from "./API/function"
import * as PusherPushNotifications from "@pusher/push-notifications-web";
import { Input } from "antd";
const { TextArea } = Input;


const beamsClient = new PusherPushNotifications.Client({
  instanceId: "e9131849-c6fa-4fba-830d-c3e2fda3598d",
});

beamsClient
  .start()
  .then(() => beamsClient.addDeviceInterest("hello"))
  .then(() => console.log("Successfully registered and subscribed!"))
  .catch(console.error);

const App = () => {
  const [message, setMessage] = useState("");
  const [users,setUsers] = useState([]);
  const [selectedUsers,setSelectedUsers] = useState([])


  const fetchData = useCallback(async () => {

    try {
      const res = await getUsers();
      if (res.data) {
        setUsers(res.data);
      }
    } catch (err) {
    console.log("err", err);

    }
  }, []);


  useEffect (() =>{
    fetchData()


  }, []);



  const handleMessageChange = ({ target: { value } }) => {
    setMessage(value);
  };

  const handleSend = async () => {
    console.log("yuui", selectedUsers)
    await sendMessageNotification(message, );
  };


  const handleChange = ({ target: { value, options } }) => {
    const selectedUserName = options[options.selectedIndex].getAttribute("name");
    const userArray = [...selectedUsers];
    const userIndex = users.findIndex((user) => user.name === selectedUserName);

    if (userIndex !== -1) {
      userArray[userIndex] = value;
      setSelectedUsers(userArray);
    }
  };
console.log("selectedUsers", selectedUsers);




console.log("selectedUsers", selectedUsers);


  return (

    <div className="container">
      <div className="textarea">

        <TextArea
          showCount
          maxLength={100}
          value={message}
          onChange={()=>handleMessageChange()}
          style={{
            width: 500,
            height: 120,
            resize: "none",

          }}
        />
      </div>
<div>
  <br/>

<label htmlFor="myDropdown">Select an option:</label>
<select id="myDropdown" value={""} onChange={handleChange}>
  <option value="">-- Select --</option>

  {users.map((option) => (
    <option key={option.name} value={option.user_id} name={option.name}>
      {option.name}
    </option>
  ))}
</select>

</div>
<br/> 


      <div className="send-container">


        <button onClick={handleSend} className="send-button">
          Send Message
        </button>
      </div>
    </div>
  );
};

export default App;
